import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

const Sidebar = () => {
    const navigate = useNavigate()
    const [open, setOpen] = useState(false)
    const [userRole, setUserRole] = useState({
        username: '',
        password: '',
        role: {
            page1: false,
            page2: false,
            page3: false,
            page4: false
        }
    })
    const [activeUser, setActiveUser] = useState('')


    function handleChange(event) {
        const { name, value, type, checked } = event.target;
        if (type === 'checkbox') {
            setUserRole(prevState => ({
            ...prevState,
            role: {
                ...prevState.role,
                [name]: checked,
            }
            }));
        } else {
            setUserRole(prevState => ({
            ...prevState,
            [name]: value,
            }));
        }
    }
    function handleRole(event) {
        event.preventDefault()
        localStorage.setItem(`roles${userRole.username}`, JSON.stringify(userRole))
        setOpen(false)
    }
    function handleLogout() {
        localStorage.removeItem('activeUser')
        navigate('/login')
    }

    useEffect(() => {
        setActiveUser(JSON.parse(localStorage.getItem(localStorage.getItem('activeUser'))))
    }, [])

    
  return (
    <div className="sidebar">
      <ul>
        <li><a href="!#" onClick={() => navigate('/')}>Dashboard</a></li>
        <li><a onClick={ 
            activeUser?.role?.page1 ? () => navigate('/page-1'): ()=>  alert("You don't have access to view this page")
        }>Page 1</a></li>
        <li><a href="!#" onClick={ 
            activeUser?.role?.page2 ? () => navigate('/page-2'): ()=>  alert("You don't have access to view this page")
        }>Page 2</a></li>
        <li><a href="!#" onClick={ 
            activeUser?.role?.page3 ? () => navigate('/page-3'): ()=>  alert("You don't have access to view this page")
        }>Page 3</a></li>
        <li><a href="!#" onClick={ 
            activeUser?.role?.page4 ? () => navigate('/page-4'): ()=>  alert("You don't have access to view this page")
        }>Page 4</a></li>
       
        {  activeUser?.username === 'admin' && <li><button onClick={() => setOpen(true)}>Add Role</button></li>}
        
        <li><button onClick={handleLogout}>Logout</button></li>
      </ul>

      {
        open &&
        <div id="myModal" className="modal">
            <div className="modal-content">
            <span className="close" style={{ cursor:'pointer'}} onClick={() => setOpen(false)}>&times;</span>
            <h2>Add Role</h2>
            <form id="loginForm">
                <div className="input-group">
                <label for="username">Username:</label>
                <input type="text" id="username" name="username" required value={userRole.username} onChange={handleChange}/>
                </div>
                <div className="input-group">
                <label for="password">Password:</label>
                <input type="password" id="password" name="password" required value={userRole.password} onChange={handleChange}/>
                </div>
                <div className="checkbox-group">
                <label><input type="checkbox" name="page1" value={userRole.role.page1} onChange={handleChange}/>Page 1</label>
                <label><input type="checkbox" name="page2" value={userRole.role.page2} onChange={handleChange}/>Page 2</label>
                <label><input type="checkbox" name="page3" value={userRole.role.page3} onChange={handleChange}/>Page 3</label>
                <label><input type="checkbox" name="page4" value={userRole.role.page4} onChange={handleChange}/>Page 4</label>
                </div>
                <button onClick={handleRole}>Add Role</button>
            </form>
            </div>
        </div>

      }
    </div>
  )
}

export default Sidebar
